import React from 'react'
import { FaUserMinus, FaUserPlus } from 'react-icons/fa'
import { Link, useNavigate } from 'react-router-dom'
import styled from 'styled-components'
import { useProductsContext } from '../context/products_context'
import { useAuth } from '../context/AuthContext'
import { auth } from "../firebase"
import {
   signOut,
} from "firebase/auth";
const CartButton = () => {
  const { closeSidebar } = useProductsContext()
  const { user } = useAuth()
  const navigate = useNavigate();

  const logout = async () => {
    await signOut(auth);
  };

  return (
    <Wrapper className='cart-btn-wrapper'>
      {/* <Link to='/login'>
        <button type='button' className='auth-btn' onClick={closeSidebar} >
            Login <FaUserPlus />
          </button>
        </Link> */}
       {user ? (
          <button
          type='button'
          className='auth-btn'
          onClick={()=>{
            closeSidebar()
            navigate('/');
            logout()
          }}
        >
          Logout <FaUserMinus />
        </button>
       
      ) : (
        <Link to='/login'>
        <button type='button' className='auth-btn' onClick={closeSidebar} >
            Login <FaUserPlus />
          </button>
        </Link>
      )} 
    </Wrapper>
  )
}

const Wrapper = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  align-items: center;
  justify-items: center;
  width: 225px;
  .cart-btn {
    color: var(--clr-grey-1);
    font-size: 1.5rem;
    letter-spacing: var(--spacing);
    color: var(--clr-grey-1);
    display: flex;
    align-items: center;
  }
  .cart-container {
    display: flex;
    align-items: center;
    position: relative;
    svg {
      height: 1.6rem;
      margin-left: 5px;
    }
  }
  .cart-value {
    position: absolute;
    top: -10px;
    right: -16px;
    background: var(--clr-primary-5);
    width: 16px;
    height: 16px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    font-size: 0.75rem;
    color: var(--clr-white);
    padding: 12px;
  }
  .auth-btn {
    display: flex;
    align-items: center;
    background: transparent;
    border-color: transparent;
    font-size: 1.5rem;
    cursor: pointer;
    color: var(--clr-grey-1);
    letter-spacing: var(--spacing);
    svg {
      margin-left: 5px;
    }
  }
`
export default CartButton
